import React, { useEffect, useState } from 'react';
import { getProducts } from '../../services/productService';
import './ProductList.css';

const ProductList = () => {
  const [products, setProducts] = useState(null); // Initialize as null for initial loading state
  const [error, setError] = useState(null); // State to store error message

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const data = await getProducts();
        if (Array.isArray(data)) {
          setProducts(data);
        } else {
          setError('Products data is not an array.');
        }
      } catch (error) {
        console.error('Error fetching products:', error);
        setError('Failed to fetch products.');
      }
    };

    fetchProducts();
  }, []);

  if (error) {
    return <div>Error: {error}</div>; // Display error message if fetch or data format error occurs
  }

  if (products === null) {
    return <div>Loading...</div>; // Show loading indicator while fetching data
  }

  return (
    <div className="product-list">
      {products.map((product) => (
        <div className={`product-tile ${!product.inStock ? 'out-of-stock' : ''}`} key={product._id}>
          <h2>{product.name}</h2>
          <p>${product.price}</p>
          {!product.inStock && <p className="out-of-stock-warning">Out of Stock</p>}
        </div>
      ))}
    </div>
  );
};

export default ProductList;
